import React, { Component } from 'react'
import Step1 from './Component/Step1'
import Step2 from './Component/Step2'
import Step3 from './Component/Step3'
import Review from './Component/Review'




import { Tabs } from 'antd';
const { TabPane } = Tabs;


class MainTab extends Component {
    constructor(props){
      super(props);
      this.state={
        selectMeal:"",
        selectPeople:"",
        selectRestaurant:"",
        selectDish:"",
        servings:"",
      }
      this.handleChange = this.handleChange.bind(this);
    }


    callback=(key)=> {
        console.log(key);
      }

    handleChange=(e)=>{
        this.setState({
            [e.target.name]:e.target.value
        });
    }
    render() {
        const {selectMeal,selectPeople,selectRestaurant,selectDish,servings}=this.state;
        return (
            <div>
      <Tabs defaultActiveKey="1" onChange={this.callback}>
      <TabPane tab="Step 1" key="1">
      <span> Please Select a Meal : <br/> {selectMeal}</span> 

      <Step1
       selectMeal={selectMeal}
       selectPeople={selectPeople}
       handleChange={this.handleChange}
       />
      </TabPane>
     <TabPane tab="Step 2" key="2">
      <Step2
       selectRestaurant={selectRestaurant}
       handleChange={this.handleChange}
      />
     </TabPane>
     <TabPane tab="Step 3" key="3">
    <span> Please Select a Dish : {selectDish} </span>
     <Step3
      selectDish={selectDish}
      servings={servings}
      handleChange={this.handleChange}

       />
     </TabPane>
     <TabPane tab="Review" key="4">
       <span> {selectRestaurant}</span>
     <Review
      // selectRestaurant ={selectRestaurant}
      // handleChange={this.handleChange}/>
      />
     </TabPane>
  </Tabs>

            </div>
        )
    }
}

export default MainTab
