import React, { Component } from 'react'
import Step1 from './Component/Step1'
import Step2 from './Component/Step2'
import Step3 from './Component/Step3'
import Review from './Component/Review'
import { Tabs } from 'antd';

const { TabPane } = Tabs;


class MainTab extends Component {
    constructor(props){
      super(props);
      this.state={
        selectMeal:"",
        selectPeople:"",
        selectRestaurant:"",
        selectDish:"",
        servings:"",
        key:"",
        defaultActiveKey:"0"
      }
      this.buttonClick = this.buttonClick.bind(this);
    }


    // buttonClick=(key)=> {
    //     console.log("key is pressed");
    //   }
    buttonClick=(key)=> {
      console.log("key is pressed");
      this.setState({key:this.state.key+1});
    }

    handleChange=(e)=>{
        this.setState({
            [e.target.name]:e.target.value
        });
    }
    render() {
        const {selectMeal,selectPeople,selectRestaurant,selectDish,servings}=this.state;
        return (
            <div>
      <Tabs
       defaultActiveKey="1"


       >

      <TabPane tab="Step 1" key="1" id="1"   onChange={this.buttonClick}      
 >
      <Step1 
       selectMeal={selectMeal}
       selectPeople={selectPeople}
       handleChange={this.handleChange}
       />
      </TabPane>

     <TabPane tab="Step 2" key="2" id="2">
      <Step2 
       selectRestaurant={selectRestaurant}
       handleChange={this.handleChange}
      />
     </TabPane>

     <TabPane tab="Step 3" key="3" id="3">
     <Step3
      selectDish={selectDish}
      servings={servings}
      handleChange={this.handleChange}/>
     </TabPane>

     <TabPane tab="Review" key="4" id="4">
      <Review
      selectMeal={selectMeal}
      selectPeople={selectPeople}
      selectRestaurant ={selectRestaurant}
      selectDish={selectDish}
      servings={servings}
      />
    </TabPane>
  </Tabs>
            </div>
        )
    }
}

export default MainTab
