import React, { Component } from 'react'
import Step1 from './Component/Step1'
import Step2 from './Component/Step2'
import Step3 from './Component/Step3'
import Review from './Component/Review'
import { Tabs } from 'antd';
import Last from './Last'

const { TabPane } = Tabs;


class MainTab extends Component {
    constructor(props){
      super(props);
      this.state={
        selectMeal:"",
        selectPeople:"",
        selectRestaurant:"",
        dishes:[
          {"value":"Chicken Burger","value":"Ham Burger"}
        ],
        servings:"",
        next:"1",
    }

   }

     buttonClick=(next)=> {
      this.setState({ next});
    }

    handledishesChanges=(e)=>{
      let {dishes} =this.state;
      // dishes.push(e.target.value )
      if (dishes.indexOf(e.target.value) === -1)
       dishes.push(e.target.value);
       this.setState({dishes})  
       }
       if(dishes=dishes.value=="Chicken Burger")
       {
         return Murga
       }
    handleChange=(e)=>{
         this.setState({
           [e.target.name]:e.target.value
         })
       }

    render() {

        const {selectMeal,selectPeople,selectRestaurant,dishes,servings,next}=this.state;

        return (
            <div>
      <Tabs 
       activeKey={next.toString()} >

      <TabPane tab="Step 1" key="1" >
        <span>Meals :</span>
      <Step1 
       triggerParentUpdate= {this.buttonClick} 
       selectMeal={selectMeal}
       selectPeople={selectPeople}
       handleChange={this.handleChange}
       />
      </TabPane>

     <TabPane tab="Step 2" key="2">
      <Step2 
      triggerParentUpdate= {this.buttonClick}
       selectRestaurant={selectRestaurant}
       handleChange={this.handleChange}
      />
     </TabPane>

     <TabPane tab="Step 3" key="3" >
       <span>Dish :</span>
     <Step3 
      triggerParentUpdate= {this.buttonClick} 
      dishes={dishes}
      servings={servings}
      handleChange={this.handleChange}
      handledishesChanges={this.handledishesChanges}/>
     </TabPane>

     <TabPane tab="Review" key="4" >
      <Review 
      triggerParentUpdate= {this.buttonClick}
      selectMeal={selectMeal}
      selectPeople={selectPeople}
      selectRestaurant ={selectRestaurant}
      dishes={dishes}
      servings={servings}
      />
    </TabPane>

    <TabPane tab="" key="5" >
       <Last/>
       </TabPane>
     </Tabs>
            </div>
        )
    }
}

export default MainTab
