import React, { Component } from 'react'
import Step1 from './Component/Step1'
import Step2 from './Component/Step2'
import Step3 from './Component/Step3'
import Review from './Component/Review'
import { Tabs } from 'antd';

const { TabPane } = Tabs;


class MainTab extends Component {
    constructor(props){
      super(props);
      this.state={
        selectMeal:"",
        selectPeople:"",
        selectRestaurant:"",
        dishes:[],
        servings:"",
        next:"1",
        showdata: this.displayData


     }
      this.buttonClick = this.buttonClick.bind(this);
      this.appendData = this.appendData.bind(this);
    }

     buttonClick=(next)=> {
      this.setState({ next});
    }
//     appendData() {
//       this.displayData.push(
//       <div> <pre> {this.state.selectDish}</pre></div>);
//       this.setState({
//          showdata: this.displayData,
//          selectDish: ""
//       });
//  }

 handledishesChanges=(e)=>{
      let {dishes} =this.state;
      dishes.push(e.target.value)
      this.setState({dishes})  
       }

    render() {

        const {selectMeal,selectPeople,selectRestaurant,dishes,servings,next}=this.state;

        return (
            <div>
      <Tabs 
       activeKey={next.toString()} >

      <TabPane tab="Step 1" key="1" counter="1"  >
        <span>Meals :</span>
      <Step1 
       triggerParentUpdate= {this.buttonClick} 
       selectMeal={selectMeal}
       selectPeople={selectPeople}
       handleChange={this.handleChange}
       />
      </TabPane>

     <TabPane tab="Step 2" key="2"   counter="2">
      <Step2 
      triggerParentUpdate= {this.buttonClick}
       selectRestaurant={selectRestaurant}
       handleChange={this.handleChange}
      />
     </TabPane>

     <TabPane tab="Step 3" key="3" counter="3">
       <span>Dish :</span>
     <Step3 
      triggerParentUpdate= {this.buttonClick} 
      dishes={dishes}
      servings={servings}
      handledishesChanges={this.handledishesChanges}/>
     </TabPane>

     <TabPane tab="Review" key="4" counter="4">
      <Review 
      triggerParentUpdate= {this.buttonClick}
      selectMeal={selectMeal}
      selectPeople={selectPeople}
      selectRestaurant ={selectRestaurant}
      dishes={dishes}
      servings={servings}
      />
    </TabPane>
  </Tabs>
            </div>
        )
    }
}

export default MainTab
