import React, { Component } from 'react'
import { InputNumber } from 'antd';

class Step3 extends Component {
    onChange=(v)=> {
        this.setState({
            [v.target.name]:v.target.value
        })
        console.log('value');
      }
    render() {
        let {selectDish,servings}=this.props
        return (
            <div>
      <select name="selectDish" defaultValue={selectDish} onChange={this.props.handleChange} style={{ width: 120 }} >
      <option value="jack">Jack</option>
      <option value="lucy">Lucy</option> 
      </select>
      Please Select No. of Servings : {servings}
      <InputNumber name="servings" value={this.onChange} defaultValue={0} onChange={this.props.handleChange} style={{ width: 90 }}/>
          {console.log({servings})}  </div>
        )
    }
}

export default Step3
