import React, { Component } from 'react'
import { Button, Icon } from 'antd';
import { Row, Col } from 'antd';



export class Review extends Component {
    constructor(props){
        super(props);
       this.state={
               Meal:"",
               No_of_People:"",
               Restaurant:"",
               Dish:""
       }}
    handleSubmit=(e)=>{
        e.preventDefault(); 
        console.log(this.state)
         }  



    render() {
        const { selectMeal,selectRestaurant,selectDish,servings}=this.props;
        const { Meal,No_of_People,Restaurant,Dish}=this.state
        return (
             <div className="color">

    <Row>
      <Col span={24} style={{margin:"30px",display:"flex"}} >Meal : {selectMeal}</Col>
      <Col span={24} style={{margin:"30px",display:"flex"}}>No. of People :{servings} </Col>
      <Col span={24} style={{margin:"30px",display:"flex"}}>
          Restaurant :{selectRestaurant} </Col>
     <Col span={24} style={{margin:"30px",display:"flex"}}>Dishes : {selectDish}</Col>
    </Row>

    <Button type="primary" style={{margin:"50px"}} onClick={()=>this.props.triggerParentUpdate(3)}> <Icon type="left" />Previous</Button>

      <input type="text" name="Meal" value={selectMeal} onChange={this.props.handleChange}/>
      <input type="text" name="No_of_People" value={servings} onChange={this.props.handleChange}/>
      <input type="text" name="Restaurant" value={selectRestaurant} onChange={this.props.handleChange}/>







     <Button onClick={this.handleSubmit} > Submit <Icon type="right" /></Button>



    </div>     
     )
    }
}

export default Review
