import React, { Component } from 'react'
import { Button, Icon } from 'antd';
import { Row, Col } from 'antd';



export class Review extends Component {
    constructor(props){
        super(props);
        this.state={
          selectMeal:"",
          selectPeople:"",
          selectRestaurant:"",
          selectDish:"",
          servings:"",
        }
       }
    handleSubmit=(e)=>{
        e.preventDefault();
        console.log(this.state)
      }
    render() {
        const {selectMeal,selectRestaurant,selectDish,servings}=this.props;

        return (
             <div className="color">
           <form  onSubmit={this.handleSubmit}>
    <Row>
      <Col span={24} style={{margin:"30px",display:"flex"}} value="">Meal : {selectMeal}</Col>
      <Col span={24} style={{margin:"30px",display:"flex"}}>No. of People :{servings} </Col>
      <Col span={24} style={{margin:"30px",display:"flex"}}>
          Restaurant :{selectRestaurant} </Col>
     <Col span={24} style={{margin:"30px",display:"flex"}}>Dishes : {selectDish}</Col>
    </Row>

    <Button type="primary" style={{margin:"50px"}} onClick={()=>this.props.triggerParentUpdate(3)}> <Icon type="left" />Previous</Button>
     <Button type="primary" onClick={()=>this.props.triggerParentUpdate(4)}  type="submit"> Submit <Icon type="right" /></Button>

     {/* <input  value={this.props.selectMeal} handleChange={this.props.handleChange} />

    <Button type="primary" type="submit"> Submit <Icon type="right" /></Button>  */}
     </form> 

    </div>     




        )
    }
}

export default Review
